
//MAKE ARRAYS

var quotes = [
"Put on your coding hats!",
"I found out that I spent all of last quarter using ‘Netflix and Chill’ wrong. I didn’t know what it meant",
"What does Thirsty mean?",
"Am I snarky?",
"You guys don’t get into enough trouble as designers",
"You guys gotta tell me what I can or can't use. I don't know any better.",
"I’ve gotten into trouble using the eggplant emoji. Can’t use that.",
"I promise this all comes together in the end",
"I’m doing the whole 30, I can’t have rice. I’m Asian, do you know how hard it is for me to not have rice?",
"Brent is my friend	",
"I have a reminder to go pick up my daughter from school",
"I don't handle any of my money, my wife keeps track of it all.",
"Come to my office hours",
"All of the good stuff goes in the curly braces",
"Yes, I have my own ipsum.",
"We're going to use Cornflowerblue",
"You're used to Eaglenet not working, right?",
"Someone spent a lot of money to put a stick on a building that shows you a color",
"I spend a lot of time on dating sites",
"Heads up, I didn't brush my teeth this morning",
"I'm looking at a room full of people who have ideas"
];

var colors = [
"red",
"orange",
"blue",
"green",
"yellow"
];

var icons = [
"big comment icon",
"big quote right icon",
"big desktop icon",
"big power icon",
"big thumbs up icon"
];


//pick a random quote, Color, and Icon to start on
var currentQuote;

firstCall();
randomQuote();



$(document).mousedown(function(){
	currentQuote = randomQuote();
});


$(document).keydown(function(){
	if(event.which == 37)
		currentQuote = left(currentQuote);
	if(event.which == 39)
		currentQuote = right(currentQuote);
	if(event.which == 32)
		currentQuote = right(currentQuote);
});


function left(currentQuote){
	//console.log(currentQuote);
	
	if(currentQuote == 0)
		currentQuote = quotes.length - 1;
	else
		currentQuote = currentQuote - 1;

	//console.log(currentQuote + " New Num");

	$('p').text(quotes[currentQuote]);
	randomClasses();

	return currentQuote;
	
};

function right(currentQuote){
	if(currentQuote == quotes.length)
		currentQuote = 0;
	else
		currentQuote = currentQuote + 1;


	$('p').text(quotes[currentQuote]);
	randomClasses();
	return currentQuote;
};


function firstCall(){
	var maxColor = colors.length;
	var ranColor = Math.floor(Math.random() * maxColor);
	$('#content').addClass( colors[ranColor] );

	var maxIcon = icons.length;
	var ranIcon = Math.floor(Math.random()* maxIcon);
	$('i').addClass(icons[ranIcon]);
	return currentQuote;
};


function randomClasses(){
	var maxColor = colors.length;
	var ranColor = Math.floor(Math.random() * maxColor);
	$("#content").attr("class", "");
	$('#content').addClass( colors[ranColor] );

	var maxIcon = icons.length;
	var ranIcon = Math.floor(Math.random()* maxIcon);
	$("i").attr("class", "");
	$('i').addClass( icons[ranIcon] );

	//console.log(ranIcon + " " + ranColor);
};

function randomQuote(){
	var maxQuote = quotes.length;
	var ranQuote = Math.floor(Math.random() * maxQuote);
	currentQuote = ranQuote;

	$('p').text(quotes[ranQuote]);
	randomClasses();
	return currentQuote;
	//console.log("Ran Random Classes");
}; 